import { setFillStyle, fillRectangle } from '../core/graphics.js'
import { EVENT_TYPE } from '../core/engine.js'
import GameObject from './GameObject.js'

// Default width and height of the placeholder
const PlaceholderDimension = {
    height: 20,
    width: 20
}

const PlaceholderType = {
    empty: 1,
    symbol: 2,
    rule: 3
}

class Placeholder extends GameObject {
    constructor(rule, x, y) {
        super()
        this.position = { x, y }
        this.width = PlaceholderDimension.width
        this.height = PlaceholderDimension.height

        // Settings
        this.type = PlaceholderType.empty
        this.value = null

        // Colors
        this.defaultColor = "#ccc"
        this.hoverColor = "#aaa"
        this.color = this.defaultColor

        // Rule of which the placeholder is part of
        this.rule = rule
    }

    /**
     * Checks if the placeholder collides with given (x, y)-coordinate. 
     * Note: This is used only when the placeholder is empty.
     * @param {int} x X-coordinate
     * @param {int} y Y-coordinate
     */
    collides(x, y) {
        return this.position.x <= x && x <= this.position.x + this.width && 
               this.position.y <= y && y <= this.position.y + this.height
    }

    /**
     * Returns the width of the placeholder.
     */
    getWidth() {
        if (this.type === PlaceholderType.rule) {
            return this.value.getWidth()
        }
        return this.width
    }

    /**
     * Sets a new position for the placeholder.
     * @param {int} x New X-coordinate
     * @param {int} y New Y-coordinate
     */
    updatePosition(x, y) {
        this.position.x = x
        this.position.y = y
        if (this.type === PlaceholderType.rule) {
            this.value.updatePosition(this.position.x, this.position.y)
        }
    }

    render(ctx) {
        switch(this.type) {
        case PlaceholderType.empty:
            setFillStyle(ctx, this.color)
            fillRectangle(ctx, this.position.x, this.position.y, this.width, this.height)
            break
        case PlaceholderType.symbol:
            // TODO: Implement
            break
        case PlaceholderType.rule:
            this.value.render(ctx)
            break
        }
    }

    /**
     * Connect rule if there exists an active rule and 
     * the placeholder collides with the given x and y coordinates
     * @param {[rule]} rules Array of rules given from the rule editor
     * @param {int} x X-coordinate
     * @param {int} y Y-coordinate
     */
    handleRuleConnection(rules, x, y) {
        if (this.collides(x, y)) {
            let [ruleId, rule] = Object.entries(rules).filter(([_, rule]) => rule.selected)[0]
            if (rule && ruleId !== this.rule.id) {
                this.value = rule 
                this.value.setConnected()
                this.type = PlaceholderType.rule
                delete rules[ruleId]
            }
        }
    }

    onEvent(type, event, rules) {
        if (this.type === PlaceholderType.empty) {
            if (type === EVENT_TYPE.mouseDown) {
                if (this.collides(event.x, event.y)) {
                    alert("To be implemented")
                } 
            } else if (type === EVENT_TYPE.mouseMove ) {
                this.color = this.collides(event.x, event.y) ? this.hoverColor : this.defaultColor
            } else if (type === EVENT_TYPE.mouseUp && this.collides(event.x, event.y)) {
                this.handleRuleConnection(rules, event.x, event.y)
            } 

            // TODO: Add connection to touch screen
        } else if (this.type === PlaceholderType.rule) {
            this.value.onEvent(type, event, rules)
            if (!this.value.connected) {
                rules[this.value.id] = this.value
                this.type = PlaceholderType.empty
                this.rule.dragged = false
                this.rule.selected = false
                this.value = null
            }
        }
    }
}

export {
    Placeholder,
    PlaceholderDimension,
    PlaceholderType
}

import GameObject from './GameObject.js'
import { DeductionRule } from './DeductionRule.js'

class RuleEditor extends GameObject {
    constructor () {
        super()
        this.rules = {}
    }

    addRule(x, y, ruleType, conjective) {
        const ruleId = Math.random().toString(36).substring(7)
        this.rules[ruleId] = new DeductionRule(ruleId, x, y, ruleType, conjective)
    }

    render(ctx) {
        for (let rule of Object.values(this.rules)) {
            rule.render(ctx)
        }
    }

    onEvent(type, event) {
        for (let rule of Object.values(this.rules)) {
            rule.onEvent(type, event, this.rules)
        }
    }

    update () {
        for (let rule of Object.values(this.rules)) {
            rule.update()
        }
    }
}

export {
    RuleEditor
}

import { clearCanvas } from './graphics.js'

const EVENT_TYPE = {
    mouseDown: 1,
    mouseUp: 2,
    mouseMove: 3,
    touchStart: 4,
    touchEnd: 5,
    touchCancel: 6,
    touchMove: 7
}


/**
 * Resize the canvas to the screen
 * @param {canvas} canvas canvas (html-element)
 */
const resizeCanvasToScreen = (canvas) => () => {
    canvas.width = window.innerWidth
    canvas.height = window.innerHeight
}

/**
 * Returns ctx (context 2D) and canvas (html-element)
 * @param {string} canvasId Id for newly created canvas
 */
const setupCanvas = (canvasId) => {
    const canvas = document.createElement("canvas")
    canvas.setAttribute("id", canvasId)

    const resize = resizeCanvasToScreen(canvas)
    resize() 

    window.addEventListener('resize', resize, false)
    window.addEventListener('orientationchange', resize, false)

    document.body.appendChild(canvas)
    const ctx = canvas.getContext("2d")

    return { canvas, ctx }
}

/**
 * Catch event, stop its default method and from propagating.
 * @param {event} event Event from a listener
 */
const catchEvent = (event) => {
    event.stopPropagation()
    event.preventDefault()
}

/**
 * Run engine with given objects
 * @param {context2d} ctx context2d from canvas
 * @param {canvas} canvas canvas (html-element)
 * @param {[DeductionRule]} content Array of deduction rules
 */
const runEngine = (ctx, canvas, content) => {
    window.onload = () => {
        const update = () => {
            content.forEach((object) => {
                object.update()
            })
        }

        const render = (ctx) => {
            clearCanvas(ctx, canvas)
            ctx.beginPath()
            ctx.save()

            content.forEach((object) => {
                object.render(ctx)
            })

            ctx.restore()
        }

        const mainloop = () => {
            update()
            render(ctx)
            requestAnimationFrame(mainloop)
        }

        canvas.addEventListener('mousedown', (event) => {
            catchEvent(event)
            content.forEach((object) => {
                object.onEvent(EVENT_TYPE.mouseDown, event)
            })
        })

        canvas.addEventListener('mouseup', (event) => {
            catchEvent(event)
            content.forEach((object) => {
                object.onEvent(EVENT_TYPE.mouseUp, event)
            })
        })

        canvas.addEventListener('mousemove', (event) => {
            catchEvent(event)
            content.forEach((object) => {
                object.onEvent(EVENT_TYPE.mouseMove, event)
            })
        })

        canvas.addEventListener('touchstart', (event) => {
            catchEvent(event)
            content.forEach((object) => {
                object.onEvent(EVENT_TYPE.touchStart, event)
            })
        })

        canvas.addEventListener('touchend', (event) => {
            catchEvent(event)
            content.forEach((object) => {
                object.onEvent(EVENT_TYPE.touchEnd, event)
            })
        })

        canvas.addEventListener('touchcancel', (event) => {
            catchEvent(event)
            content.forEach((object) => {
                object.onEvent(EVENT_TYPE.touchCancel, event)
            })
        })

        canvas.addEventListener('touchmove', (event) => {
            catchEvent(event)
            content.forEach((object) => {
                object.onEvent(EVENT_TYPE.touchMove, event)
            })
        })

        mainloop()
    }
}

export {
    EVENT_TYPE,
    setupCanvas,
    runEngine
}

